User.all.map do |user|
  user.basic_post_credits = 0
  user.plus_post_credits = 0
  user.pro_post_credits = 0
  user
end.each(&:save)
users = User.where(user_type: :regular_user)
company_names = %w[Nikola Motor Company ]
plan_types = ['basic', 'plus', 'pro']
months = 12
(months * 5).times do
  purchase_data = {
    plan_type: plan_types[rand(3)],
    paid: true,
    created_at: rand(months * 30).days.ago
  }
  purchase = Purchase.new(purchase_data)
  purchase.set_price
  purchase.user = users[rand(users.count)]
  purchase.save
  purchase.update_user_credits
end